function combine(arr){
  arr = arr.filter(s => s && s.length);
  if(arr.length == 1) return arr[0]
  if(arr.length == 2) return arr.join(" and ")
  else return arr.slice(0,arr.length-1).join(', ') + ', and ' + arr[arr.length-1]
}

